import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const JobListings = () => {
    const [jobs, setJobs] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [location, setLocation] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Fetch jobs from API
        const fetchJobs = async () => {
            try {
                const response = await axios.get('https://api.example.com/jobs'); // Replace with your API endpoint
                setJobs(response.data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching jobs:', error);
                setLoading(false);
            }
        };

        fetchJobs();
    }, []);

    const filteredJobs = jobs.filter(job => {
        const matchesSearch = job.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
            job.company.toLowerCase().includes(searchTerm.toLowerCase());
        const matchesLocation = location === '' || job.location.toLowerCase().includes(location.toLowerCase());
        return matchesSearch && matchesLocation;
    });

    return (
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
            <div className="mb-8">
                <h1 className="text-3xl font-bold text-gray-900 mb-4">Find Your Next Opportunity</h1>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <input
                        type="text"
                        placeholder="Search jobs..."
                        className="border border-gray-300 rounded-md px-4 py-2"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                    <input
                        type="text"
                        placeholder="Location"
                        className="border border-gray-300 rounded-md px-4 py-2"
                        value={location}
                        onChange={(e) => setLocation(e.target.value)}
                    />
                    <button className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700">
                        Search
                    </button>
                </div>
            </div>

            {loading ? (
                <div className="text-center py-8">Loading...</div>
            ) : (
                <div className="grid grid-cols-1 gap-6">
                    {filteredJobs.map((job) => (
                        <div key={job.id} className="bg-white shadow rounded-lg p-6">
                            <div className="flex justify-between items-start">
                                <div>
                                    <h3 className="text-lg font-medium text-gray-900">{job.title}</h3>
                                    <p className="mt-1 text-gray-600">{job.company}</p>
                                    <p className="mt-1 text-sm text-gray-500">{job.location}</p>
                                </div>
                                <div className="text-right">
                                    <p className="text-sm text-gray-500">Posted {job.postedDate}</p>
                                    <Link
                                        to={`/jobs/${job.id}`}
                                        className="mt-2 inline-block bg-blue-600 text-white px-4 py-2 rounded-md text-sm hover:bg-blue-700"
                                    >
                                        View Details
                                    </Link>
                                </div>
                            </div>
                            <div className="mt-4">
                                <p className="text-gray-600">{job.description.substring(0, 150)}...</p>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default JobListings;
