// src/components/JobCard.js
import React from 'react';
import { Link } from 'react-router-dom';

const JobCard = ({ job }) => {
    // Format salary function
    const formatSalary = (job) => {
        // Check for direct salary range from API
        if (job.job_min_salary && job.job_max_salary) {
            const currency = job.job_salary_currency || 'USD';
            const minSalary = new Intl.NumberFormat('en-US', {
                style: 'currency',
                currency: currency,
                maximumFractionDigits: 0
            }).format(job.job_min_salary);

            const maxSalary = new Intl.NumberFormat('en-US', {
                style: 'currency',
                currency: currency,
                maximumFractionDigits: 0
            }).format(job.job_max_salary);

            return `${minSalary} - ${maxSalary} per year`;
        }

        // Check for salary in job description
        const description = job.description.toLowerCase();
        const salaryRegex = /(?:salary|compensation|pay):?\s*([\$£€¥]?\s*\d+[,\d]*(?:\s*-\s*[\$£€¥]?\s*\d+[,\d]*)?(?:\s*k)?)/i;
        const match = description.match(salaryRegex);

        if (match) {
            return `${match[1]} (estimated from description)`;
        }

        // If employer name is available, show "Competitive salary"
        if (job.company) {
            return "Competitive salary";
        }

        return "Salary range available upon application";
    };

    // Format date
    const formatDate = (dateString) => {
        const date = new Date(dateString);
        const now = new Date();
        const diffTime = Math.abs(now - date);
        const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

        if (diffDays === 1) return 'Posted yesterday';
        if (diffDays < 7) return `Posted ${diffDays} days ago`;
        if (diffDays < 30) return `Posted ${Math.floor(diffDays / 7)} weeks ago`;
        return `Posted ${Math.floor(diffDays / 30)} months ago`;
    };

    return (
        <div className="bg-white shadow-lg rounded-lg p-6 mb-4 hover:shadow-xl transition-shadow duration-200">
            <div className="flex flex-col md:flex-row justify-between">
                <div className="flex-1">
                    <h2 className="text-xl font-semibold text-gray-800 mb-2">{job.title}</h2>
                    <div className="mb-4">
                        <p className="text-gray-600">{job.company}</p>
                        <p className="text-gray-500">{job.location}</p>
                    </div>

                    {/* Salary with improved formatting */}
                    <div className="mb-4">
                        <p className="text-green-600 font-medium">
                            {formatSalary(job)}
                        </p>
                    </div>

                    {/* Job Type */}
                    <div className="mb-4">
                        <span className="bg-blue-100 text-blue-800 text-sm font-medium px-3 py-1 rounded">
                            {job.type}
                        </span>
                    </div>

                    {/* Requirements section */}
                    {job.requirements && job.requirements.length > 0 && (
                        <div className="mb-4">
                            <h3 className="text-lg font-medium text-gray-800 mb-2">Key Requirements:</h3>
                            <ul className="list-disc list-inside text-gray-600">
                                {job.requirements.slice(0, 3).map((req, index) => (
                                    <li key={index} className="mb-1">{req}</li>
                                ))}
                            </ul>
                        </div>
                    )}

                    {/* Posted Date */}
                    <p className="text-sm text-gray-500 mt-4">
                        {formatDate(job.createdAt)}
                    </p>
                </div>

                {/* Actions */}
                <div className="mt-4 md:mt-0 md:ml-6 flex flex-col justify-center space-y-2">
                    <Link
                        to={`/jobs/${job._id}`}
                        className="inline-flex items-center justify-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700"
                    >
                        View Details
                    </Link>
                    <a
                        href={job.url}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="inline-flex items-center justify-center px-4 py-2 border border-indigo-600 text-sm font-medium rounded-md text-indigo-600 bg-white hover:bg-indigo-50"
                    >
                        Apply Now
                    </a>
                </div>
            </div>
        </div>
    );
};

export default JobCard;
