// src/pages/JobDetails.js
import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { jobsData } from '../data/jobsData';

const JobDetails = () => {
    const { id } = useParams();
    const [job, setJob] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchJobDetails = async () => {
            try {
                setLoading(true);
                // First try to find the job in our jobsData
                const foundJob = jobsData.find(j => j.job_id === id);

                if (foundJob) {
                    setJob(foundJob);
                    setError(null);
                } else {
                    // If not found in local data, try API
                    const options = {
                        method: 'GET',
                        url: `https://jsearch.p.rapidapi.com/job-details`,
                        params: {
                            job_id: id
                        },
                        headers: {
                            'X-RapidAPI-Key': 'e09cede52dmsh581a47e5d817d93p16ffaejsn51e0434c6a96',
                            'X-RapidAPI-Host': 'jsearch.p.rapidapi.com'
                        }
                    };

                    const response = await fetch(options.url, options);
                    const data = await response.json();

                    if (data.data && data.data.length > 0) {
                        setJob(data.data[0]);
                        setError(null);
                    } else {
                        throw new Error('Job not found');
                    }
                }
            } catch (err) {
                setError('Job not found');
                setJob(null);
            } finally {
                setLoading(false);
            }
        };

        fetchJobDetails();
    }, [id]);

    if (loading) {
        return (
            <div className="max-w-4xl mx-auto p-6">
                <div className="animate-pulse">
                    <div className="h-8 bg-gray-200 rounded w-1/4 mb-4"></div>
                    <div className="h-6 bg-gray-200 rounded w-1/3 mb-4"></div>
                    <div className="h-4 bg-gray-200 rounded w-full mb-4"></div>
                    <div className="h-4 bg-gray-200 rounded w-full mb-4"></div>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="max-w-4xl mx-auto p-6">
                <div className="text-center">
                    <h2 className="text-2xl font-bold text-gray-900 mb-4">Job not found</h2>
                    <p className="text-gray-600 mb-4">The job you're looking for doesn't exist or has been removed.</p>
                    <Link
                        to="/jobs"
                        className="inline-block bg-indigo-600 text-white px-6 py-2 rounded-md hover:bg-indigo-700 transition-colors"
                    >
                        Back to Jobs
                    </Link>
                </div>
            </div>
        );
    }

    if (!job) return null;

    return (
        <div className="max-w-4xl mx-auto p-6">
            <div className="bg-white rounded-lg shadow-lg p-6">
                <h1 className="text-3xl font-bold text-gray-900 mb-4">{job.job_title}</h1>

                <div className="mb-6">
                    <h2 className="text-xl font-semibold text-gray-800">{job.employer_name}</h2>
                    <p className="text-gray-600">{job.job_city}, {job.job_country}</p>
                </div>

                {/* Salary Information */}
                {(job.job_min_salary || job.job_max_salary) && (
                    <div className="mb-6">
                        <h3 className="text-lg font-semibold text-gray-800 mb-2">Salary Range</h3>
                        <p className="text-green-600 font-medium">
                            {new Intl.NumberFormat('en-IN', {
                                style: 'currency',
                                currency: job.job_salary_currency || 'INR',
                                maximumFractionDigits: 0
                            }).format(job.job_min_salary)} - {
                                new Intl.NumberFormat('en-IN', {
                                    style: 'currency',
                                    currency: job.job_salary_currency || 'INR',
                                    maximumFractionDigits: 0
                                }).format(job.job_max_salary)}
                        </p>
                    </div>
                )}

                {/* Job Description */}
                <div className="mb-6">
                    <h3 className="text-lg font-semibold text-gray-800 mb-2">Job Description</h3>
                    <p className="text-gray-600 whitespace-pre-line">{job.job_description}</p>
                </div>

                {/* Requirements */}
                {job.job_highlights?.Qualifications && (
                    <div className="mb-6">
                        <h3 className="text-lg font-semibold text-gray-800 mb-2">Requirements</h3>
                        <ul className="list-disc list-inside text-gray-600">
                            {job.job_highlights.Qualifications.map((req, index) => (
                                <li key={index} className="mb-1">{req}</li>
                            ))}
                        </ul>
                    </div>
                )}

                {/* Employment Type */}
                <div className="mb-6">
                    <h3 className="text-lg font-semibold text-gray-800 mb-2">Employment Type</h3>
                    <p className="text-gray-600">{job.job_employment_type || 'Full-time'}</p>
                </div>

                {/* Posted Date */}
                <div className="mb-6">
                    <h3 className="text-lg font-semibold text-gray-800 mb-2">Posted Date</h3>
                    <p className="text-gray-600">
                        {new Date(job.job_posted_at_datetime_utc).toLocaleDateString()}
                    </p>
                </div>

                {/* Action Buttons */}
                <div className="flex gap-4">
                    <a
                        href={job.job_apply_link}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="inline-block bg-indigo-600 text-white px-6 py-2 rounded-md hover:bg-indigo-700 transition-colors"
                    >
                        Apply Now
                    </a>
                    <Link
                        to="/jobs"
                        className="inline-block border border-gray-300 text-gray-700 px-6 py-2 rounded-md hover:bg-gray-50 transition-colors"
                    >
                        Back to Jobs
                    </Link>
                </div>
            </div>
        </div>
    );
};

export default JobDetails;
