// src/components/JobCard.js
import React from 'react';
import { Link } from 'react-router-dom';

const JobCard = ({ job }) => {
    const formatSalary = (min, max, currency = 'INR') => {
        if (!min && !max) return "Competitive salary";

        const formatter = new Intl.NumberFormat('en-IN', {
            style: 'currency',
            currency: currency,
            maximumFractionDigits: 0
        });

        if (min && max) {
            return `${formatter.format(min)} - ${formatter.format(max)}`;
        } else if (min) {
            return `From ${formatter.format(min)}`;
        } else if (max) {
            return `Up to ${formatter.format(max)}`;
        }
    };

    const formatDate = (dateString) => {
        const date = new Date(dateString);
        const now = new Date();
        const diffTime = Math.abs(now - date);
        const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

        if (diffDays === 1) return 'Posted yesterday';
        if (diffDays < 7) return `Posted ${diffDays} days ago`;
        if (diffDays < 30) return `Posted ${Math.floor(diffDays / 7)} weeks ago`;
        return `Posted ${Math.floor(diffDays / 30)} months ago`;
    };

    return (
        <div className="bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200 p-6">
            <div className="flex flex-col md:flex-row justify-between">
                <div className="flex-1">
                    {/* Job Title */}
                    <h2 className="text-xl font-semibold text-gray-900 mb-2">
                        {job.title}
                    </h2>

                    {/* Company and Location */}
                    <div className="mb-4">
                        <p className="text-gray-700">{job.company}</p>
                        <p className="text-gray-600">{job.location}</p>
                    </div>

                    {/* Salary */}
                    <div className="mb-4">
                        <p className="text-green-600 font-medium">
                            {formatSalary(job.job_min_salary, job.job_max_salary, job.job_salary_currency)}
                        </p>
                    </div>

                    {/* Job Type */}
                    <div className="mb-4">
                        <span className="bg-blue-100 text-blue-800 text-sm font-medium px-3 py-1 rounded">
                            {job.type}
                        </span>
                    </div>

                    {/* Requirements (showing first 2) */}
                    {job.requirements && job.requirements.length > 0 && (
                        <div className="mb-4">
                            <ul className="list-disc list-inside text-gray-600">
                                {job.requirements.slice(0, 2).map((req, index) => (
                                    <li key={index} className="text-sm mb-1">{req}</li>
                                ))}
                            </ul>
                        </div>
                    )}

                    {/* Posted Date */}
                    <p className="text-sm text-gray-500">
                        {formatDate(job.createdAt)}
                    </p>
                </div>

                {/* Action Buttons */}
                <div className="mt-4 md:mt-0 md:ml-6 flex flex-col space-y-2">
                    <Link
                        to={`/jobs/${job._id}`}
                        className="inline-flex items-center justify-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 transition-colors"
                    >
                        View Details
                    </Link>
                    {job.url && (
                        <a
                            href={job.url}
                            target="_blank"
                            rel="noopener noreferrer"
                            className="inline-flex items-center justify-center px-4 py-2 border border-indigo-600 text-sm font-medium rounded-md text-indigo-600 bg-white hover:bg-indigo-50 transition-colors"
                        >
                            Apply Now
                        </a>
                    )}
                </div>
            </div>
        </div>
    );
};

export default JobCard;
