// src/pages/JobListings.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import JobCard from '../components/JobCard';
import SearchBar from '../components/SearchBar';
import InfiniteScroll from 'react-infinite-scroll-component';
import { jobsData } from '../data/jobsData';
import config from '../config/config';

const JobListings = () => {
    const [jobs, setJobs] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [searchTerm, setSearchTerm] = useState('');
    const [location, setLocation] = useState('');
    const [page, setPage] = useState(1);
    const [hasMore, setHasMore] = useState(true);
    const [currentQuery, setCurrentQuery] = useState('Software Developer in India');
    const [usingFallbackData, setUsingFallbackData] = useState(false);

    const fetchJobs = async (query = currentQuery, pageNum = 1, isNewSearch = false) => {
        const options = {
            method: 'GET',
            url: 'https://jsearch.p.rapidapi.com/search',
            params: {
                query: query,
                page: pageNum.toString(),
                num_pages: '1',
                date_posted: 'all'
            },
            headers: {
                'X-RapidAPI-Key': config.rapidApi.key,
                'X-RapidAPI-Host': config.rapidApi.host
            }
        };

        try {
            if (pageNum === 1) {
                setLoading(true);
            }
            setError('');
            
            const response = await axios.request(options);
            
            if (response.data && response.data.data) {
                if (isNewSearch) {
                    setJobs(response.data.data);
                } else {
                    setJobs(prevJobs => [...prevJobs, ...response.data.data]);
                }
                setHasMore(response.data.data.length >= 10);
                setUsingFallbackData(false);
            } else {
                throw new Error('No data received from API');
            }
        } catch (error) {
            console.error('Error fetching jobs:', error);
            
            // Use fallback data if it's the first page
            if (pageNum === 1) {
                console.log('Using fallback data');
                setJobs(jobsData);
                setHasMore(false);
                setUsingFallbackData(true);
            } else {
                setError('Failed to load more jobs');
            }
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchJobs(currentQuery, 1, true);
    }, []);

    const handleSearch = () => {
        const searchQuery = `${searchTerm} ${location ? 'in ' + location : ''}`.trim();
        setCurrentQuery(searchQuery || 'Software Developer in India');
        setPage(1);
        fetchJobs(searchQuery || 'Software Developer in India', 1, true);
    };

    const loadMore = () => {
        if (!usingFallbackData) {
            const nextPage = page + 1;
            setPage(nextPage);
            fetchJobs(currentQuery, nextPage, false);
        }
    };

    const filterFallbackJobs = () => {
        if (!searchTerm && !location) return jobsData;
        
        return jobsData.filter(job => {
            const matchesSearch = job.job_title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                                job.job_description.toLowerCase().includes(searchTerm.toLowerCase());
            const matchesLocation = !location || 
                                  job.job_city.toLowerCase().includes(location.toLowerCase()) ||
                                  job.job_country.toLowerCase().includes(location.toLowerCase());
            return matchesSearch && matchesLocation;
        });
    };

    const LoadingSpinner = () => (
        <div className="flex items-center justify-center py-4">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-indigo-600"></div>
        </div>
    );

    return (
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
            <div className="mb-8">
                <h1 className="text-3xl font-bold text-gray-900 mb-4">Job Listings</h1>
                {usingFallbackData && (
                    <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4 mb-4">
                        <div className="flex">
                            <div className="flex-shrink-0">
                                <svg className="h-5 w-5 text-yellow-400" viewBox="0 0 20 20" fill="currentColor">
                                    <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                                </svg>
                            </div>
                            <div className="ml-3">
                                <p className="text-sm text-yellow-700">
                                    Currently showing sample job listings. Live job data is temporarily unavailable.
                                </p>
                            </div>
                        </div>
                    </div>
                )}
                <SearchBar
                    searchTerm={searchTerm}
                    setSearchTerm={setSearchTerm}
                    location={location}
                    setLocation={setLocation}
                    onSearch={handleSearch}
                />
            </div>

            {loading && page === 1 ? (
                <LoadingSpinner />
            ) : error ? (
                <div className="text-center py-8">
                    <p className="text-red-600 mb-4">{error}</p>
                    <button 
                        onClick={() => fetchJobs(currentQuery, 1, true)} 
                        className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition-colors"
                    >
                        Try Again
                    </button>
                </div>
            ) : jobs.length === 0 ? (
                <div className="text-center py-8">
                    <p className="text-gray-500">No jobs found matching your criteria.</p>
                </div>
            ) : (
                <InfiniteScroll
                    dataLength={jobs.length}
                    next={loadMore}
                    hasMore={!usingFallbackData && hasMore}
                    loader={<LoadingSpinner />}
                >
                    <div className="grid grid-cols-1 gap-6">
                        {(usingFallbackData ? filterFallbackJobs() : jobs).map((job, index) => (
                            <JobCard 
                                key={`${job.job_id}-${index}`}
                                job={{
                                    _id: job.job_id,
                                    title: job.job_title,
                                    company: job.employer_name,
                                    location: `${job.job_city || ''} ${job.job_country || ''}`.trim(),
                                    description: job.job_description,
                                    requirements: 
                                        job.job_highlights?.Qualifications || 
                                        job.job_highlights?.Requirements ||
                                        [],
                                    job_min_salary: job.job_min_salary,
                                    job_max_salary: job.job_max_salary,
                                    job_salary_currency: job.job_salary_currency,
                                    type: job.job_employment_type || 'Full-time',
                                    createdAt: job.job_posted_at_datetime_utc,
                                    url: job.job_apply_link
                                }} 
                            />
                        ))}
                    </div>
                </InfiniteScroll>
            )}
        </div>
    );
};

export default JobListings;
