import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/common.css';

const Profile = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [showAuthData, setShowAuthData] = useState(false);

    useEffect(() => {
        const userData = JSON.parse(localStorage.getItem('user'));
        const token = localStorage.getItem('token');
        
        if (!userData || !token) {
            navigate('/login');
            return;
        }

        setUser(userData);
        setLoading(false);
    }, [navigate]);

    if (loading) {
        return (
            <div className="min-h-screen flex items-center justify-center">
                <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-600"></div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
            <div className="max-w-3xl mx-auto">
                <div className="bg-white shadow rounded-lg overflow-hidden">
                    <div className="px-4 py-5 sm:px-6">
                        <h3 className="text-lg leading-6 font-medium text-gray-900">Profile Information</h3>
                        <p className="mt-1 max-w-2xl text-sm text-gray-500">Personal details and authentication data.</p>
                    </div>
                    <div className="border-t border-gray-200 px-4 py-5 sm:px-6">
                        <dl className="grid grid-cols-1 gap-x-4 gap-y-8 sm:grid-cols-2">
                            <div className="sm:col-span-1">
                                <dt className="text-sm font-medium text-gray-500">Full name</dt>
                                <dd className="mt-1 text-sm text-gray-900">{user.name}</dd>
                            </div>
                            <div className="sm:col-span-1">
                                <dt className="text-sm font-medium text-gray-500">Email address</dt>
                                <dd className="mt-1 text-sm text-gray-900">{user.email}</dd>
                            </div>
                            <div className="sm:col-span-1">
                                <dt className="text-sm font-medium text-gray-500">Account type</dt>
                                <dd className="mt-1 text-sm text-gray-900">{user.role || 'User'}</dd>
                            </div>
                            <div className="sm:col-span-1">
                                <dt className="text-sm font-medium text-gray-500">Member since</dt>
                                <dd className="mt-1 text-sm text-gray-900">
                                    {new Date(user.createdAt).toLocaleDateString()}
                                </dd>
                            </div>
                        </dl>
                    </div>
                    
                    {/* Authentication Data Section */}
                    <div className="border-t border-gray-200 px-4 py-5 sm:px-6">
                        <div className="flex items-center justify-between">
                            <h3 className="text-lg leading-6 font-medium text-gray-900">Authentication Data</h3>
                            <button
                                onClick={() => setShowAuthData(!showAuthData)}
                                className="text-indigo-600 hover:text-indigo-900 text-sm font-medium"
                            >
                                {showAuthData ? 'Hide' : 'Show'} Auth Data
                            </button>
                        </div>
                        {showAuthData && (
                            <div className="mt-4 bg-gray-50 p-4 rounded-md">
                                <div className="space-y-4">
                                    <div>
                                        <dt className="text-sm font-medium text-gray-500">Stored Token</dt>
                                        <dd className="mt-1 text-sm text-gray-900 break-all">
                                            {localStorage.getItem('token')}
                                        </dd>
                                    </div>
                                    <div>
                                        <dt className="text-sm font-medium text-gray-500">User Data</dt>
                                        <dd className="mt-1 text-sm text-gray-900">
                                            <pre className="bg-white p-2 rounded overflow-auto">
                                                {JSON.stringify(user, null, 2)}
                                            </pre>
                                        </dd>
                                    </div>
                                </div>
                            </div>
                        )}
                    </div>

                    <div className="px-4 py-4 bg-gray-50 sm:px-6 flex justify-end space-x-3">
                        <button
                            onClick={() => navigate('/jobs')}
                            className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                        >
                            View Jobs
                        </button>
                        <button
                            onClick={() => navigate('/profile/edit')}
                            className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-indigo-700 bg-indigo-100 hover:bg-indigo-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                        >
                            Edit Profile
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Profile;
